import streamlit as st

st.title("Chai test poll")
col1,col2 = st.columns(2)

with col1:
    st.header("Masala Chai")
    st.image("data:image/jpeg;base64,")

    vote1 = st.button("Vote Masala chai")

with col2:
    st.header("adhrak Chai")
    st.image("data:image/jpeg;base64,")
    vote2 = st.button("Vote adhrak chai")


if vote1:
    st.success("Thanks for voting masala chai")
elif vote2:
    st.success("Thanks for voting adhark chai")

name = st.sidebar.text_input("Enter your name:")
tea = st.sidebar.selectbox("Choose your chai:",["masala","kesar","adhrak"])
st.write(f"welcome, {name} and your chai {tea}")

with st.expander("Show chai making instructions:"):
    st.write(""" 
        1. Boil Water with tea leaves
        2. Add milk and spices
        3. Server hot.
     """)
    
st.markdown('### Welcome to chai app')
st.markdown('> Blockquote')